{
  "where": {
    "text_matches": {
      "must": [
        {
          "text_value": "director manager vice president",
          "filter_key": "title",
          "search_type": "shuffle",
          "operator": "or",
          "fuzzy": true
        }
      ]
    },
    "keyword_match": {
      "must": {
        "company_industries": ["Software", "Technology"],
        "company_country": ["united states"],
        "seniority": ["executive", "principal"],
        "departments": ["Sales", "Marketing", "Product"],
        "email_status": ["verified"]
      }
    },
    "range_query": {
      "must": {
        "company_annual_revenue": {
          "gte": 10000000
        },
        "company_employees_count": {
          "gte": 200
        }
      }
    }
  },
  "select_columns": ["id", "first_name", "last_name", "title", "email", "linkedin_url", "company_id"],
  "company_config": {
    "populate": true,
    "select_columns": ["uuid", "name", "employees_count", "annual_revenue", "website"]
  },
  "order_by": [
    {
      "order_by": "seniority",
      "order_direction": "desc"
    }
  ],
  "page": 1,
  "limit": 50
}

