scripts.postResponse:
if (rq.response.code === 200){
const jsonData = rq.response.json();
const authData = jsonData.data?.auth;
if (authData){
const result = authData.login || authData.register || authData.refreshToken;
if (result){
if (result.accessToken){
rq.environment.set("accessToken",result.accessToken);
}
if (result.refreshToken){
rq.environment.set("refreshToken",result.refreshToken);
}
if (result.user && result.user.uuid){
rq.environment.set("userId",result.user.uuid);
}
}
}
}

