"query":"query ComplexCompanySearch($query:VQLQueryInput!){ companies{ companyQuery(query:$query){ items{ uuid name employeesCount industries technologies annualRevenue totalFunding city country} total}}}",
"variables":{
"query":{
"filters":{
"conditions":[
{
"field":"name",
"operator":"contains",
"value":"tech software"
},
{
"field":"industries",
"operator":"in",
"value":[
"Software",
"SaaS",
"Technology"
]
},
{
"field":"technologies",
"operator":"in",
"value":[
"Python",
"Go",
"AWS"
]
},
{
"field":"employees_count",
"operator":"gte",
"value":100
},
{
"field":"employees_count",
"operator":"lte",
"value":5000
},
{
"field":"annual_revenue",
"operator":"gte",
"value":5000000
},
{
"field":"country",
"operator":"in",
"value":[
"united states",
"canada",
"united kingdom"
]
}
]
},
"orderBy":[
{
"orderBy":"annual_revenue",
"orderDirection":"desc"
},
{
"orderBy":"employees_count",
"orderDirection":"desc"
}
],
"limit":50,
"page":1
}
}
}

