{
"query":"query GeographicTargets($query:VQLQueryInput!){ companies{ companyQuery(query:$query){ items{ uuid name employeesCount industries city state country website} total}}}",
"variables":{
"query":{
"filters":{
"conditions":[
{
"field":"industries",
"operator":"in",
"value":[
"Software",
"SaaS"
]
},
{
"field":"employees_count",
"operator":"gte",
"value":100
},
{
"field":"city",
"operator":"in",
"value":[
"San Francisco",
"New York",
"Austin",
"Seattle"
]
},
{
"field":"state",
"operator":"in",
"value":[
"California",
"New York",
"Texas",
"Washington"
]
},
{
"field":"country",
"operator":"eq",
"value":"united states"
}
]
},
"orderBy":[
{
"orderBy":"employees_count",
"orderDirection":"desc"
},
{
"orderBy":"city",
"orderDirection":"asc"
}
],
"limit":200,
"page":1
}
}
}

