{
"query":"query ComplexContactSearch($query:VQLQueryInput!){ contacts{ contactQuery(query:$query){ items{ uuid firstName lastName email title departments seniority country createdAt} total}}}",
"variables":{
"query":{
"filters":{
"conditions":[
{
"field":"title",
"operator":"contains",
"value":"engineer developer"
},
{
"field":"departments",
"operator":"in",
"value":[
"Engineering",
"Product"
]
},
{
"field":"seniority",
"operator":"in",
"value":[
"Senior",
"Lead",
"Principal"
]
},
{
"field":"country",
"operator":"in",
"value":[
"united states",
"canada",
"united kingdom"
]
},
{
"field":"email_status",
"operator":"eq",
"value":"verified"
},
{
"field":"created_at",
"operator":"gte",
"value":"2023-01-01T00:00:00Z"
}
]
},
"orderBy":[
{
"orderBy":"created_at",
"orderDirection":"desc"
},
{
"orderBy":"last_name",
"orderDirection":"asc"
}
],
"limit":50,
"page":1
}
}
}

