{
"query":"query ContactsWithCompany($query:VQLQueryInput!){ contacts{ contactQuery(query:$query){ items{ uuid firstName lastName title companyUuid company{ uuid name employeesCount industries annualRevenue website}} total}}}",
"variables":{
"query":{
"filters":{
"conditions":[
{
"field":"company_industries",
"operator":"in",
"value":[
"Software",
"SaaS"
]
},
{
"field":"seniority",
"operator":"in",
"value":[
"Senior",
"Lead"
]
},
{
"field":"email_status",
"operator":"eq",
"value":"verified"
}
]
},
"selectColumns":[
"uuid",
"first_name",
"last_name",
"title",
"company_id"
],
"companyConfig":{
"populate":true,
"selectColumns":[
"uuid",
"name",
"employees_count",
"industries",
"annual_revenue",
"website"
]
},
"limit":25,
"page":1
}
}
}

