{
"query":"query ContactsAtHighValueCompanies($query:VQLQueryInput!){ contacts{ contactQuery(query:$query){ items{ uuid firstName lastName email title seniority companyUuid} total}}}",
"variables":{
"query":{
"filters":{
"conditions":[
{
"field":"company_industries",
"operator":"in",
"value":[
"Software",
"SaaS",
"Technology"
]
},
{
"field":"company_annual_revenue",
"operator":"gte",
"value":5000000
},
{
"field":"company_employees_count",
"operator":"gte",
"value":100
},
{
"field":"company_employees_count",
"operator":"lte",
"value":1000
},
{
"field":"seniority",
"operator":"in",
"value":[
"Senior",
"Lead",
"Principal",
"Executive"
]
},
{
"field":"email_status",
"operator":"eq",
"value":"verified"
}
]
},
"orderBy":[
{
"orderBy":"seniority",
"orderDirection":"desc"
}
],
"limit":100,
"page":1
}
}
}

