"conditions":[
{
"field":"company_industries",
"operator":"in",
"value":[
"Software",
"SaaS",
"Technology"
]
},
{
"field":"company_annual_revenue",
"operator":"gte",
"value":1000000
},
{
"field":"company_employees_count",
"operator":"gte",
"value":50
},
{
"field":"company_employees_count",
"operator":"lte",
"value":1000
},
{
"field":"seniority",
"operator":"in",
"value":[
"Senior",
"Lead",
"Principal",
"Executive"
]
},
{
"field":"departments",
"operator":"in",
"value":[
"Sales",
"Marketing",
"Business Development"
]
},
{
"field":"email_status",
"operator":"eq",
"value":"verified"
},
{
"field":"country",
"operator":"in",
"value":[
"united states",
"canada",
"united kingdom"
]
}
]
},
"companyConfig":{
"populate":true,
"selectColumns":[
"uuid",
"name",
"employees_count",
"industries",
"annual_revenue",
"website"
]
},
"orderBy":[
{
"orderBy":"seniority",
"orderDirection":"desc"
}
],
"limit":200,
"page":1
}
}
}

