"operator":"in",
"value":[
"Python",
"Go",
"React",
"AWS"
]
},
{
"field":"company_employees_count",
"operator":"gte",
"value":100
},
{
"field":"country",
"operator":"in",
"value":[
"united states",
"canada"
]
}
]
},
"orderBy":[
{
"orderBy":"created_at",
"orderDirection":"desc"
}
],
"limit":50,
"page":1
}
}
}

