{
"query":"mutation CreateExportJob($input:CreateExportJobInput!){ jobs{ createExportJob(input:$input){ uuid jobType status data createdAt}}}",
"variables":{
"input":{
"service":"contact",
"vql":{
"where":{
"keywordMatch":{
"must":{
"country":[
"united states"
],
"title":[
"VP",
"Director"
]
}
}
},
"selectColumns":[
"first_name",
"last_name",
"email",
"title",
"uuid"
],
"orderBy":[
{
"orderBy":"created_at",
"orderDirection":"desc"
}
],
"limit":500
},
"s3Bucket":"my-bucket",
"retryCount":2
}
}
}

