{
  "query": "query { admin { userStats { totalUsers activeUsers usersByRole { role count } usersByPlan { plan count } } } }",
  "variables": {}
}

{
  "query": "query ListActivities($input: ActivityFilterInput) { activities { activities(input: $input) { items { id serviceType actionType status createdAt } total limit offset hasNext } } }",
  "variables": {
    "input": {
      "limit": 5,
      "offset": 0
    }
  }
}

{
  "query": "query ListExports($input: ExportFilterInput) { exports { exports(input: $input) { items { id name status progress createdAt } total hasNext } } }",
  "variables": {
    "input": {
      "limit": 20,
      "offset": 0
    }
  }
}

{
  "query": "query GetUsage($featureKey: String) { usage { usage(featureKey: $featureKey) { features { featureKey used limit remaining percentage resetAt isUnlimited } } } }",
  "variables": {}
}
